INSERT INTO products(sku,brand,product_title,product_desc,sale_price,msrp,quantity,star_rating,catagory) VALUES (134514,'Gatewaytofail','Gateway 2000 10th Anniversary PC (1995)','instead of Cristal champagne, buyers got Boone''s Farm--the so-called 6X CD-ROM spun at 4X or slower (a big performance hit in 1995), the video card was a crippled version of what people thought they were getting, and the surround-sound speakers weren''t actually surround-capable. Perhaps Gateway was sticking to the traditional gift for a tenth anniversary: It''s tin, not gold.',169.99,150.00,1,4.8,'computer');
